import React from 'react';
import { Image, Text, TouchableOpacity, View } from 'react-native';
import { COLORS, FONTS } from '../constants/theme';

const ProductCardStyle2 = ({image,title,category,imageUrl,onPress}) => {
    return (
        <TouchableOpacity
            activeOpacity={.9}
            onPress={onPress}
            style={{
                borderRadius:6,
                borderWidth:1,
                borderColor:COLORS.borderColor,
            }}
        >
            <Image
                style={{
                    width:'100%',
                    height:undefined,
                    aspectRatio:100/100,
                    borderTopLeftRadius:4,
                    borderTopRightRadius:4,
                }}
                source={imageUrl ? {uri : imageUrl} : image}
            />
            <View
                style={{alignItems:'center',paddingHorizontal:8,paddingVertical:10}}
            >
                <Text style={{...FONTS.fontXs,...FONTS.fontBold}}>{category}</Text>
                <Text style={{...FONTS.fontSm,...FONTS.fontBold,color:COLORS.title}}>{title}</Text>
            </View>
        </TouchableOpacity>
    );
};

export default ProductCardStyle2;
