const MainHome = ({navigation}) => {

    

    return (
        <SafeAreaView
            style={{
                flex:1,
                backgroundColor:COLORS.backgroundColor,
            }}
        >
            <View
                style={{
                    flexDirection:'row',
                    alignItems:'center',
                    height:45,
                    borderBottomWidth:1,
                    borderBottomColor:COLORS.borderColor,
                }}
            >
                <IconButton
                    icon={() => 
                        <View style={{
                            borderWidth:1,
                            borderColor:COLORS.borderColor,
                            height:30,
                            width:30,
                            alignItems:'center',
                            justifyContent:'center',
                            borderRadius:8,
                        }}><FeatherIcon color={COLORS.title} size={18} name='menu'/></View>}
                    size={25}
                    onPress={() => navigation.openDrawer()}
                />
                <Text style={{...FONTS.font,...FONTS.fontBold,color:COLORS.title,flex:1,bottom:1,marginLeft:5}}>Home</Text>
                <IconButton
                    icon={() => <FeatherIcon color={COLORS.title} size={20} name='search'/>}
                    size={25}
                    onPress={() => navigation.navigate('Search')}
                />
                <IconButton
                    icon={() => <FeatherIcon color={COLORS.title} size={20} name='heart'/>}
                    size={25}
                    onPress={() => navigation.navigate('Wishlist')}
                />
                <IconButton
                    onPress={() => navigation.navigate('Cart')}
                    icon={() => 
                        <View>
                            <FeatherIcon color={COLORS.title} size={20} name='shopping-cart'/>
                            <View
                                style={{
                                    height:14,
                                    width:14,
                                    borderRadius:14,
                                    backgroundColor:COLORS.primary,
                                    alignItems:'center',
                                    justifyContent:'center',
                                    position:'absolute',
                                    top:-4,
                                    right:-6,
                                }}
                            >
                                <Text style={{...FONTS.fontXs,fontSize:10,color:COLORS.white}}>2</Text>
                            </View>
                        </View>
                    }
                    size={25}
                />
            </View>
            <ScrollView>
                
                <Swiper
                    autoplay={true}
                    autoplayTimeout={6}
                    height={'auto'}
                    dotColor={'rgba(255,255,255,.3)'}
                    activeDotColor={COLORS.white}
                    paginationStyle={{bottom:10}}
                >
                    {bannerData.map((data,index) => {
                        return(
                            <View
                                key={index}
                            >
                                <LinearGradient 
                                    colors={['transparent', 'transparent', 'rgba(0,0,0,.4)']} 
                                    style={{
                                        position:'absolute',
                                        height:'100%',
                                        width:'100%',
                                        zIndex:1,
                                    }}>
                                </LinearGradient>
                                <Image
                                    style={{
                                        width:'100%',
                                        height:undefined,
                                        aspectRatio : 250 / 100,
                                    }}
                                    source={data.image}
                                />
                            </View>
                        )
                    })}
                </Swiper>

                <View
                    style={{
                        flexDirection:'row',
                        flexWrap:'wrap',
                        marginTop:20,
                    }}
                >
                    {categoryData.map((data,index) => {
                        return(
                            <View
                                key={index}
                                style={{
                                    width:'25%',
                                    alignItems:'center',
                                }}
                            >
                                <TouchableOpacity
                                    onPress={() => {
                                        data.title == "More" ?
                                        navigation.navigate('Categories')
                                        :
                                        navigation.navigate('CategoryHome',{ title : data.title })
                                        //navigation.navigate('Featured')
                                    }}
                                    style={{
                                        alignItems:'center',
                                        marginBottom:18,
                                    }}
                                >
                                    <View
                                        style={{
                                            height:60,
                                            width:60,
                                            borderRadius:60,
                                            backgroundColor:'#FFF1F6',
                                            borderWidth:1,
                                            borderColor:'#FFD9E7',
                                            marginBottom:6,
                                            overflow:'hidden',
                                            alignItems:'center',
                                            justifyContent:'center',
                                        }}
                                    >
                                        {data.title == "More" ?
                                            <MaterialIcons color={COLORS.primary} size={40} name='more-horiz'/>
                                            :
                                            <Image
                                                style={{
                                                    width:"100%",
                                                    height:60,
                                                }}
                                                source={data.image}
                                            />
                                        }
                                    </View>
                                    <Text style={{...FONTS.fontXs,...FONTS.fontBold,color:COLORS.title}}>{data.title}</Text>
                                </TouchableOpacity>
                            </View>
                        )
                    })}
                </View>
                
                <ImageBackground
                    source={bg1}
                    style={{
                        height:60,
                        marginTop:8,
                        flexDirection:'row',
                        alignItems:'center',
                    }}
                >
                    <Image
                        style={{
                            width:70,
                            height:60,
                            marginLeft:20,
                            marginRight:'12%',
                        }}
                        source={offer}
                    />
                    <View style={{paddingTop:4}}>
                        <Text style={{...FONTS.fontSm,...FONTS.fontBold,color:COLORS.white}}>Sale ends in</Text>
                        <StopWatch />
                    </View>
                </ImageBackground>
                
                <View
                    style={{
                        paddingHorizontal:15,
                        flexDirection:'row',
                        alignItems:'center',
                        paddingTop:18,
                        paddingBottom:10,
                    }}
                >
                    <Text style={{...FONTS.font,...FONTS.fontBold,color:COLORS.title,flex:1}}>Suggest for You</Text>
                    <TouchableOpacity
                        onPress={() => navigation.navigate('Items',{type: "Fashion"})}
                        style={{
                            flexDirection:'row',
                            alignItems:'center',
                        }}
                    >
                        <Text style={{...FONTS.fontSm,color:COLORS.title,marginRight:2}}>View all</Text>
                        <FeatherIcon size={16} color={COLORS.title} name='chevron-right'/>
                    </TouchableOpacity>
                </View>
                <View
                    style={{
                        borderBottomWidth:1,
                        borderBottomColor:COLORS.borderColor,
                        marginBottom:25,
                    }}
                >
                    <ScrollView
                        contentContainerStyle={{paddingLeft:15}}
                        horizontal
                        showsHorizontalScrollIndicator={false}    
                    >
                        {SuggestData.map((data,index) => {
                            return(
                                <View
                                    key={index}
                                    style={{
                                        width:150,
                                        marginRight:10,
                                        marginBottom:20,
                                    }}
                                >
                                    <ProductCardStyle1
                                        onPress={() => navigation.navigate('Items',{type: "Fashion"})}
                                        image={data.image}
                                        title={data.title}
                                        price={data.price}
                                        oldPrice={data.oldPrice}
                                        offer={data.offer}
                                    />
                                </View>
                            )
                        })}
                    </ScrollView>
                </View>
                <View>
                    <View
                        style={{
                            flexDirection:'row',
                            paddingHorizontal:10,
                        }}
                    >
                        {ProductData.map((data,index) => (

                            <View 
                                key={index}
                                style={{flex:1,paddingHorizontal:5}}
                            >
                                <ProductCardStyle2  
                                    onPress={() => navigation.navigate('Items',{type: data.type})}
                                    image={data.image}
                                    category={data.category}
                                    title={data.title}
                                />
                            </View>
                        ))}
                    </View>
                </View>
                <View
                    style={{
                        paddingHorizontal:15,
                        flexDirection:'row',
                        alignItems:'center',
                        paddingTop:18,
                        paddingBottom:10,
                        borderTopWidth:1,
                        borderColor:COLORS.borderColor,
                        marginTop:20,
                    }}
                >
                    <Text style={{...FONTS.font,...FONTS.fontBold,color:COLORS.title,flex:1}}>Popular Items</Text>
                    <TouchableOpacity
                        style={{
                            flexDirection:'row',
                            alignItems:'center',
                        }}
                    >
                        <Text style={{...FONTS.fontSm,color:COLORS.title,marginRight:2}}>View all</Text>
                        <FeatherIcon size={16} color={COLORS.title} name='chevron-right'/>
                    </TouchableOpacity>
                </View>
                {PopularItemsData.map((data,index) => (
                    <ProductListItem
                        onPress={() => navigation.navigate('ProductDetail',{item : data, category : "Appliances"})}
                        key={index}
                        image={data.imagePath}
                        title={data.title}
                        desc={data.desc}
                        price={data.price}
                        oldPrice={data.oldPrice}
                        offer={data.offer}
                    />
                ))}

                <View style={{...GlobalStyleSheet.container,backgroundColor:'#151423',marginTop:15}}>
                    <View>
                        <Text style={{...FONTS.h6,marginBottom:5,color:COLORS.white}}>Top Selection</Text>
                    </View>
                    <View style={GlobalStyleSheet.row}>
                        {TopSelectionData.map((data,index) => {
                            return(
                                <View
                                    key={index}
                                    style={GlobalStyleSheet.col50}
                                >
                                    <TouchableOpacity
                                        activeOpacity={.9}
                                        onPress={() => navigation.navigate('Items',{type: data.type})}
                                        style={{
                                            backgroundColor:COLORS.white,
                                            marginBottom:10,
                                            borderRadius:6,
                                            overflow:'hidden',
                                        }}
                                    >
                                        <Image
                                            style={{
                                                width:'100%',
                                                height:undefined,
                                                aspectRatio:100/100,
                                            }}
                                            source={data.image}
                                        />
                                        <View
                                            style={{
                                                paddingHorizontal:12,
                                                paddingVertical:10,
                                            }}
                                        >
                                            <Text style={{...FONTS.font,...FONTS.fontBold,color:COLORS.title}}>{data.title}</Text>
                                            <Text style={{...FONTS.fontSm,marginTop:2}}>{data.offer}</Text>
                                        </View>
                                    </TouchableOpacity>
                                </View>
                            )
                        })}
                    </View>
                </View>

            </ScrollView>
        </SafeAreaView>
    );
};

export default MainHome;
