import React, { useState } from 'react';
import { SafeAreaView, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import { SvgXml } from 'react-native-svg';
import { GlobalStyleSheet } from '../../constants/StyleSheet';
import { COLORS, FONTS, ICONS, SIZES } from '../../constants/theme';
import Header from '../../layout/Header';
import CustomInput from '../../components/Input/CustomInput';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcon from 'react-native-vector-icons/MaterialIcons';
import MultiSlider from '@ptomasroos/react-native-multi-slider';
import DropShadow from 'react-native-drop-shadow';

const Inputs = () => {

    const [passwordShow , setPasswordShow ] = useState(true);
    
    const handndleShowPassword = () => {
        setPasswordShow(!passwordShow);
    }

    return (
        <>
            <SafeAreaView style={{flex:1,backgroundColor:COLORS.backgroundColor}}>
                <Header 
                    titleLeft
                    title={'Inputs'}  
                    leftIcon={'back'}
                />
                <ScrollView>
                    <View style={GlobalStyleSheet.container}>
                        <DropShadow
                            style={{
                                shadowColor: "#000",
                                shadowOffset: {
                                    width: 0,
                                    height: 5,
                                },
                                shadowOpacity: .15,
                                shadowRadius: 5,
                            }}
                        >
                            <View style={styles.card}>

                                <View style={{marginBottom:15}}>
                                    <Text style={{...FONTS.h6,lineHeight:18,marginBottom:2,color:COLORS.title}}>Input with icon</Text>
                                </View>

                                <View style={{marginBottom:15}}>
                                    <View style={styles.inputIcon}>
                                        <SvgXml
                                            stroke={COLORS.primary}
                                            xml={ICONS.user}
                                        />
                                    </View>
                                    <TextInput
                                        style={styles.inputStyle}
                                        placeholder='Username'
                                        placeholderTextColor={COLORS.text}
                                    />
                                </View>
                                <View style={{marginBottom:15}}>
                                    <View style={styles.inputIcon}>
                                        <SvgXml
                                            stroke={COLORS.primary}
                                            xml={ICONS.email}
                                        />
                                    </View>
                                    <TextInput
                                        style={styles.inputStyle}
                                        placeholder='Email'
                                        placeholderTextColor={COLORS.text}
                                    />
                                </View>
                                <View style={{marginBottom:15}}>
                                    <View style={styles.inputIcon}>
                                        <SvgXml
                                            fill={COLORS.primary}
                                            xml={ICONS.lock}
                                        />
                                    </View>
                                    <TextInput
                                        secureTextEntry={passwordShow}
                                        style={styles.inputStyle}
                                        placeholder='Password'
                                        placeholderTextColor={COLORS.text}
                                    />
                                    <TouchableOpacity
                                        accessible={true}
                                        accessibilityLabel="Password"
                                        accessibilityHint="Password show and hidden"
                                        onPress={() => handndleShowPassword()}
                                        style={styles.eyeIcon}>
                                        <SvgXml
                                            xml={passwordShow ? ICONS.eyeClose : ICONS.eyeOpen}
                                        />
                                    </TouchableOpacity>
                                </View>

                            </View>
                        </DropShadow>

                        <DropShadow
                            style={{
                                shadowColor: "#000",
                                shadowOffset: {
                                    width: 0,
                                    height: 5,
                                },
                                shadowOpacity: .15,
                                shadowRadius: 5,
                            }}
                        >
                            <View style={styles.card}>
                                <View style={{marginBottom:15}}>
                                    <Text style={{...FONTS.h6,lineHeight:18,marginBottom:2,color:COLORS.title}}>Modern Fields</Text>
                                </View>
                                <View style={{marginBottom:15}}>
                                    <CustomInput
                                        icon={<FontAwesome style={{opacity:.6}} name={'user'} size={20} color={COLORS.text}/> }
                                        value={''}  
                                        placeholder="Enter Username"
                                        onChangeText={(value)=> console.log(value)}
                                    />
                                </View>
                                <View style={{marginBottom:15}}>
                                    <CustomInput
                                        icon={<MaterialIcon style={{opacity:.6}} name={'email'} size={20} color={COLORS.text}/>}
                                        value={''}  
                                        placeholder="Enter Email"
                                        onChangeText={(value)=> console.log(value)}
                                    />
                                </View>
                                <View style={{marginBottom:15}}>
                                    <CustomInput
                                        type={'password'}
                                        icon={<FontAwesome style={{opacity:.6}} name={'lock'} size={20} color={COLORS.text}/> }
                                        value={''}  
                                        placeholder="Password"
                                        onChangeText={(value)=> console.log(value)}
                                    />
                                </View>
                            </View>
                        </DropShadow>

                        <DropShadow
                            style={{
                                shadowColor: "#000",
                                shadowOffset: {
                                    width: 0,
