import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import DropShadow from 'react-native-drop-shadow';
import { GlobalStyleSheet } from '../../constants/StyleSheet';
import { COLORS, FONTS } from '../../constants/theme';

const TableOddEven = () => {

    const TableData = [
        {
            name : "John Doe",
            email : "johndoe@gmail.com",
            age : 20,
        },
        {
            name : "John Doe",
            email : "johndoe@gmail.com",
            age : 20,
        },
        {
            name : "John Doe",
            email : "johndoe@gmail.com",
            age : 20,
        },
        {
            name : "John Doe",
            email : "johndoe@gmail.com",
            age : 20,
        },
    ]

    return (
        <>
            <DropShadow
                style={{
                    shadowColor: "#000",
                    shadowOffset: {
                        width: 0,
                        height: 5,
                    },
                    shadowOpacity: .15,
                    shadowRadius: 5,
                }}
            >
                <View style={{...GlobalStyleSheet.card,padding:5}}>
                    <View
                        style={{
                            flex: 1,
                            alignSelf: 'stretch',
                            flexDirection: 'row',
                        }}
                    >
                        <Text style={{...styles.theadItem,flex:.6,paddingLeft:15}}>Name</Text>
                        <Text style={{...styles.theadItem}}>Email</Text>
                        <Text style={{...styles.theadItem,flex:0.5,textAlign:'right',paddingRight:15}}>Age</Text>
                    </View>
                    {TableData.map((data,index) => {
                        return(
                            <View
                                key={index}
                                style={[{
                                    flex: 1,
                                    alignSelf: 'stretch',
                                    flexDirection: 'row',
                                } , index % 2 === 0 && {
                                    backgroundColor : '#eee',
                                }]}
                            >
                                <Text style={[{...styles.tbodyItem,flex:.6,paddingLeft:15}, index % 2 === 0 && {color : COLORS.title}]}>{data.name}</Text>
                                <Text numberOfLines={1} style={[{...styles.tbodyItem}, index % 2 === 0 && {color : COLORS.title}]}>{data.email}</Text>
                                <Text style={[{...styles.tbodyItem,flex:0.5,textAlign:'right',paddingRight:15}, index % 2 === 0 && {color : COLORS.title}]}>{data.age}</Text>
                            </View>
                        )
                    })}
                </View>
            </DropShadow>
        </>
    );
};

const styles = StyleSheet.create({
    theadItem:{
        flex: 1, 
        alignSelf: 'stretch',
        paddingHorizontal:10,
        paddingVertical:12,
        ...FONTS.font,
        color:COLORS.text,
    },
    tbodyItem:{
        flex: 1, 
        alignSelf: 'stretch',
        paddingHorizontal:10,
        paddingVertical:12,
        ...FONTS.font,
        ...FONTS.fontBold,
        color:COLORS.title,
    }
})

export default TableOddEven;
