    {
        selected : false,
        title : "40% or more",
    },
    {
        selected : false,
        title : "60% or more",
    },
    {
        selected : false,
        title : "70% or more",
    },
]
const brandFilterData = [
    {
        selected : true,
        title : "Roadster",
    },
    {
        selected : true,
        title : "Peter England",
    },
    {
        selected : true,
        title : "Flying Machine",
    },
    {
        selected : true,
        title : "Killer",
    },
    {
        selected : true,
        title : "Levi's",
    },
    {
        selected : true,
        title : "Puma",
    },
    {
        selected : true,
        title : "Wildcraft",
    },
    {
        selected : true,
        title : "Ndet",
    },
    {
        selected : true,
        title : "Woodland",
    },
]

const Items = ({navigation, route}) => {

    const sheetRef = useRef();

    const { type } = route.params;

    const Products = type == "Mobiles" ? MobilesData.items :
                    type == "Electronics" ? ElectronicsData.items :
                    type == "Furniture" ? FurnitureData.items :
                    type == "Grocery" ? GroceryData.items :
                    type == "Appliances" ? AppliancesData.items :
                    type == "Books,Toys" ? BooksToysData.items :
                    type == "Fashion" ? FashionData.items : ProductData;

    const [itemData, setItemData] = useState(Products);

    const [sortVal , setSortVal] = useState('');
    const [sheetType , setSheetType] = useState('');
    const [brandFilter , setBrandFilter] = useState(brandFilterData);
    const [discountFilter , setDiscountFilter] = useState(discountFilterData);
    const [filterData , setFilterData] = useState([]);
    const [isSnackbar , setIsSnackbar] = useState(false);
    const [snackText , setSnackText] = useState("Loading...");

    const handleItemLike = (val) => {
        let items = itemData.map((data) => {
            if(val === data.id){
                if(data.isLike){
                    setSnackText("Item removed to Favourite.");
                }else{
                    setSnackText("Item add to Favourite.");
                }
                setIsSnackbar(true);
                return { ...data, isLike : !data.isLike};
            }
            return data;
        })
        setItemData(items);
    }

    const handleFilterSelected = (val) => {
        let Brand = brandFilter.map((data) => {
            if (val === data.title) {
                return { ...data, selected: !data.selected };
            }
            return data;
        });
        let Discount = discountFilter.map((data) => {
            if (val === data.title) {
                return { ...data, selected: !data.selected };
            }
            return data;
        });
        setBrandFilter(Brand);
        setDiscountFilter(Discount);
        setFilterData(
            sheetType == "brand" ? Brand :
            sheetType == "discount" ? Discount :
            []
        )
    }

    return (
        <>
            <RBSheet
                ref={sheetRef}
                height={
                    sheetType === "sort" ? 250 :
                    sheetType === "discount" ? 310 :
                    sheetType === "brand" ? 400 :
                    300
                }
                closeOnDragDown={true}
                closeOnPressMask={true}
            >
                {sheetType == "sort" ?
                    <RadioButton.Group onValueChange={value => {setSortVal(value); sheetRef.current.close()}} value={sortVal}>
                        <RadioButton.Item color={COLORS.primary} uncheckedColor={COLORS.label} style={{paddingVertical:2}} label="What's new" value="newest" />
                        <RadioButton.Item color={COLORS.primary} uncheckedColor={COLORS.label} style={{paddingVertical:2}} label="Price - high to low" value="price-hightolow" />
                        <RadioButton.Item color={COLORS.primary} uncheckedColor={COLORS.label} style={{paddingVertical:2}} label="Price - low to hight" value="price-lowtohigh" />
                        <RadioButton.Item color={COLORS.primary} uncheckedColor={COLORS.label} style={{paddingVertical:2}} label="Popularity" value="popularity" />
                        <RadioButton.Item color={COLORS.primary} uncheckedColor={COLORS.label} style={{paddingVertical:2}} label="Discount" value="discount" />
                    </RadioButton.Group>
                    :
                    <>
                        <View
                            style={{
                                flexDirection:'row',
                                alignItems:'center',
                                paddingHorizontal:5,
                                marginTop:-10,
                                marginBottom:5,
                            }}
                        >
                            <TouchableOpacity
                                onPress={() => sheetRef.current.close()}
                                style={{
                                    padding:10,
                                    marginRight:3,
                                }}
                            >
                                <FeatherIcon color={COLORS.title} size={24} name='x'/>
                            </TouchableOpacity>
                            <Text style={{...FONTS.h6,top:1}}>Filters</Text>
                        </View>
                        <View
                            style={{
                                flexDirection:'row',
                                flexWrap:'wrap',
                            }}
                        >
                            {filterData.map((data,index) => (
                                <View
                                    key={index}
                                    style={{
                                        width:'50%',
                                    }}
                                >
                                    <List.Item 
                                        style={{paddingVertical:2}}
                                        onPress={() => handleFilterSelected(data.title)} 
                                        left={() => 
                                            <CheckBox
                                                tintColors={{ true: COLORS.primary, false: COLORS.text }}
                                                style={{left:10}}
                                                value={data.selected}
                                                disabled
                                            />
                                        }
                                        title={() => <Text  style={{...FONTS.font,...FONTS.fontMedium,top:-1,color:COLORS.title}}>{data.title}</Text>}
                                    />
                                </View>
                            ))}
                        </View>
                        <View
                            style={GlobalStyleSheet.container}
                        >
                            <View style={GlobalStyleSheet.row}>
                                <View style={GlobalStyleSheet.col50}>
                                    <TouchableOpacity
                                        style={{
                                            borderWidth:1,
                                            borderColor:COLORS.borderColor,
                                            paddingHorizontal:15,
                                            alignItems:'center',
                                            paddingVertical:14,
                                            borderRadius:SIZES.radius,
                                        }}
                                    >
                                        <Text style={{...FONTS.fontLg,color:COLORS.primary}}>Clear</Text>
                                    </TouchableOpacity>
                                </View>
                                <View style={GlobalStyleSheet.col50}>
                                    <CustomButton
                                        title={'Apply'}
                                    />
                                </View>
                            </View>
                        </View>
                    </>
                }
            </RBSheet>
            
            <SafeAreaView
                style={{
                    flex:1,
                    backgroundColor:COLORS.backgroundColor,
                }}
            >
                <Header
                    titleLeft
                    leftIcon={'back'}
                    title={type}
                />
                <View>
                    <ScrollView
                        horizontal
                        showsHorizontalScrollIndicator={false}
                    >
                        <View
                            style={{
                                paddingHorizontal:15,
                                paddingVertical:10,
                                flexDirection:'row',
                                alignItems:'center',
                            }}
                        >
                            <Ripple
                                onPress={() => {setSheetType('sort');sheetRef.current.open()}}
                                style={styles.badge}
                            >
                                <Octicons size={16} style={{marginRight:6}} name='sort-desc'/>
                                <Text style={{...FONTS.font,top:-1,color:COLORS.title}}>Sort By</Text>
                                <FeatherIcon style={{marginLeft:2,marginRight:-6}} size={18} name='chevron-down'/>
                            </Ripple>
                            <TouchableOpacity
                                onPress={() => navigation.navigate('Filter')}
                                style={styles.badge}
                            >
                                <FeatherIcon style={{marginRight:8}} size={15} name='filter' />
                                <Text style={{...FONTS.font,top:-1,color:COLORS.title}}>Filter</Text>
                            </TouchableOpacity>
                            <Ripple
                                onPress={() => {setSheetType('brand');setFilterData(brandFilter);sheetRef.current.open()}}
                                style={styles.badge}
                            >
                                <Text style={{...FONTS.font,top:-1,color:COLORS.title}}>Brand</Text>
                                <FeatherIcon style={{marginLeft:2,marginRight:-6}} size={18} name='chevron-down'/>
                            </Ripple>
                            <Ripple
                                onPress={() => {setSheetType('discount');setFilterData(discountFilter);sheetRef.current.open()}}
                                style={styles.badge}
                            >
                                <Text style={{...FONTS.font,top:-1,color:COLORS.title}}>discount</Text>
                                <FeatherIcon style={{marginLeft:2,marginRight:-6}} size={18} name='chevron-down'/>
                            </Ripple>
                        </View>
                    </ScrollView>
                </View>
                <ScrollView>
                    <View
                        style={{
                            paddingTop:5,
                        }}
                    >
                        <View style={{
                            flexDirection:'row',
                            flexWrap:'wrap',
                            paddingHorizontal:5,
                        }}>
                            {itemData.map((data,index) => (
                                <View
                                    key={index}
                                    style={{width:'50%',paddingHorizontal:5}}
                                >
                                    <ProductItem
                                        onPress={() => navigation.navigate('ProductDetail',{item : data, category : type})}
                                        imgLength={type == "Fashion"}
                                        id={data.id}
                                        imageSrc={data.image}
                                        title={data.title}
                                        desc={data.desc}
                                        status={data.status}
                                        price={data.price}
                                        oldPrice={data.oldPrice}
                                        rating={data.rating}
                                        reviews={data.reviews}
                                        isLike={data.isLike}
                                        handleItemLike={handleItemLike}
                                    />
                                </View>
                            ))}
                        </View>
                    </View>
                </ScrollView>
                <Snackbar
                    visible={isSnackbar}
                    duration={3000}
                    onDismiss={() => setIsSnackbar(false)}
                    action={{
                        label: 'Wishlist',
                        onPress: () => {
                            navigation.navigate('Wishlist');
                        },
                    }}
                >
                    {snackText}
                </Snackbar>
            </SafeAreaView>
        </>
    );
};

const styles = StyleSheet.create({
    badge : {
        borderWidth:1,
        borderColor:COLORS.borderColor,
        backgroundColor:'#f5f5f5',
        paddingHorizontal:15,
        paddingVertical:6,
        borderRadius:20,
        marginRight:12,
        flexDirection:'row',
        alignItems:'center',
    }
})

export default Items;
